        }

        public LocalModel GetNearest(Point point)
        {
            var model = list.First();

            foreach (var h in list)
            {
                if(h.Ubicacion.DistanceFrom(point) < model.Ubicacion.DistanceFrom(point))
                    model = h;
            }
            return model;
        }

